import anime from '../node_modules/animejs/lib/anime.es.js';

function animateGoal() {
  anime({
    targets: "#balID",
    easing: 'easeInOutQuad',
    scale: [1, 0.5],
    translateY: [{
      value: -800,
      duration: 1000,
      },{
      value: -500,
      duration: 1000,
    }],
    complete: function(anim) {
      document.getElementById("balID").removeAttribute("style");
    }
  });
}

function animateMiss() {
  anime({
    targets: "#balID",
    translateY: [{
      value: -800,
      duration: 1000,
    }, {
      value: -700,
      duration: 1000,
    }],
    translateX: [{
      value: 400,
      duration: 1000
    }, {
      value: 600
    }],
    scale: [1, 0.5],
    rotate: '3turn',
    easing: 'easeInOutQuad',
    duration: 2000,
    complete: function(anim) {
      document.getElementById("balID").removeAttribute("style");
    }
  });
}

export { animateGoal, animateMiss };
